import React from 'react';
import {
  List, ListItem, ListItemButton, ListItemIcon, ListItemText,
  Divider, Typography, Box, Avatar, Badge
} from '@mui/material';
import PersonIcon from '@mui/icons-material/Person';

const ContactList = ({ contacts, selectedContactId, onSelectContact }) => {
  return (
    <Box sx={{ width: '100%', bgcolor: 'background.paper' }}>
      <Typography variant="h6" sx={{ p: 2, pb: 1 }}>
        Contacts
      </Typography>
      <Divider />
      <List>
        {contacts.length === 0 ? (
          <ListItem>
            <ListItemText 
              primary="No contacts" 
              secondary="Add contacts to start messaging" 
            />
          </ListItem>
        ) : (
          contacts.map((contact) => (
            <ListItemButton
              key={contact.id}
              selected={selectedContactId === contact.id}
              onClick={() => onSelectContact(contact)}
            >
              <ListItemIcon>
                {contact.profilePictureUrl ? (
                  <Avatar src={contact.profilePictureUrl} alt={contact.username} />
                ) : (
                  <Avatar>
                    {contact.username.charAt(0).toUpperCase()}
                  </Avatar>
                )}
              </ListItemIcon>
              <ListItemText 
                primary={
                  <Box sx={{ display: 'flex', justifyContent: 'space-between' }}>
                    <Typography>{contact.username}</Typography>
                    {contact.lastMessageTime && (
                      <Typography variant="caption" color="text.secondary">
                        {new Date(contact.lastMessageTime).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
                      </Typography>
                    )}
                  </Box>
                } 
                secondary={
                  <Box sx={{ display: 'flex', justifyContent: 'space-between' }}>
                    <Typography 
                      variant="body2" 
                      noWrap 
                      sx={{ 
                        maxWidth: '180px', 
                        fontWeight: contact.unreadCount > 0 ? 'bold' : 'normal',
                        color: contact.unreadCount > 0 ? 'primary.main' : 'text.secondary'
                      }}
                    >
                      {contact.lastMessage || 'No messages yet'}
                    </Typography>
                    {contact.unreadCount > 0 && (
                      <Badge 
                        badgeContent={contact.unreadCount} 
                        color="primary" 
                        sx={{ ml: 1 }}
                      />
                    )}
                  </Box>
                }
              />
              <Box 
                sx={{ 
                  ml: 1, 
                  width: 10, 
                  height: 10, 
                  borderRadius: '50%', 
                  backgroundColor: contact.online ? 'success.main' : 'grey.400' 
                }}
              />
            </ListItemButton>
          ))
        )}
      </List>
    </Box>
  );
};

export default ContactList;
